// Parse and extract price from scraped data
const items = $input.all();
const processedItems = [];

for (const item of items) {
  const scrapedData = item.json;
  const competitorInfo = $('Loop Through Competitors').item.json.competitors[$itemIndex];

  // Extract price from various possible formats
  let price = null;
  let priceText = '';

  // Try to find price in common patterns
  if (scrapedData.price) {
    priceText = scrapedData.price;
  } else if (scrapedData.data && scrapedData.data[0]) {
    priceText = scrapedData.data[0].price || scrapedData.data[0].final_price || '';
  }

  // Clean and parse price
  if (priceText) {
    // Remove currency symbols and commas
    const cleanPrice = priceText.toString().replace(/[$¬£‚Ç¨,]/g, '').trim();
    price = parseFloat(cleanPrice);
  }

  // Calculate price difference
  const ourPrice = $('Load Competitor URLs').item.json.ourPrice;
  const priceDifference = price ? (((price - ourPrice) / ourPrice) * 100).toFixed(2) : null;
  const isUnderpriced = price && price < ourPrice;
  const percentageDiff = Math.abs(parseFloat(priceDifference));

  processedItems.push({
    json: {
      competitorName: competitorInfo.name,
      competitorUrl: competitorInfo.url,
      productName: competitorInfo.productName,
      competitorPrice: price,
      ourPrice: ourPrice,
      priceDifference: priceDifference,
      percentageDiff: percentageDiff,
      isUnderpriced: isUnderpriced,
      scrapedAt: new Date().toISOString(),
      rawData: scrapedData
    }
  });
}

return processedItems;

