// Create daily summary report
const allItems = $input.all();

if (allItems.length === 0) {
  return [{
    json: {
      summary: 'No competitor data processed today',
      totalCompetitors: 0
    }
  }];
}

const totalCompetitors = allItems.length;
const underpriced = allItems.filter(item => item.json.isUnderpriced).length;
const avgPriceDiff = (allItems.reduce((sum, item) => sum + parseFloat(item.json.priceDifference || 0), 0) / totalCompetitors).toFixed(2);
const lowestCompetitor = allItems.reduce((min, item) => 
  item.json.competitorPrice < (min.json.competitorPrice || Infinity) ? item : min
, allItems[0]);
const highestCompetitor = allItems.reduce((max, item) => 
  item.json.competitorPrice > (max.json.competitorPrice || 0) ? item : max
, allItems[0]);

return [{
  json: {
    reportDate: new Date().toISOString(),
    summary: {
      totalCompetitors: totalCompetitors,
      competitorsUnderpriced: underpriced,
      avgPriceDifference: avgPriceDiff,
      lowestPrice: {
        competitor: lowestCompetitor.json.competitorName,
        price: lowestCompetitor.json.competitorPrice
      },
      highestPrice: {
        competitor: highestCompetitor.json.competitorName,
        price: highestCompetitor.json.competitorPrice
      },
      ourPrice: allItems[0].json.ourPrice
    },
    competitorDetails: allItems.map(item => ({
      name: item.json.competitorName,
      price: item.json.competitorPrice,
      difference: item.json.priceDifference + '%',
      url: item.json.competitorUrl
    }))
  }
}];

