// We take the first item.
const item = $input.item.json;

// Character model retrieval
const modele = item["Mod√®le de personnage"];

// Base64 recovery
const imageBase64 = item["image_base64"];

// Creating a Data URL that can be used in an API
// ‚ö° Change ‚Äúimage/jpeg‚Äù if your image is PNG.
const imageUrl = `data:image/jpeg;base64,${imageBase64}`;

// Turn everything over
return [
  {
    json: {
      modele_personnage: modele,
      image_url: imageUrl
    }
  }
];

const output = items[0].json;

// Extraire le base64 depuis choices[0].message.images[0].image_url.url
let base64Data = output.choices[0].message.images[0].image_url.url;

// Enlever le pr√©fixe "data:image/png;base64,"
base64Data = base64Data.replace(/^data:image\/\w+;base64,/, "");

// Retourner dans un champ "data" ‚Üí Convert to File va l'utiliser
return [
  {
    json: {
      data: base64Data
    }
  }
];
