parameters.inputSchema:
{
  "type": "array",
  "items": {
    "type": "object",
    "properties": {
      "text": {
        "type": "string",
        "description": "Full summarized text including company overview, ratings, and insights"
      },
      "overview": {
        "type": "object",
        "properties": {
          "companyName": { "type": "string" },
          "rating": { "type": "number" },
          "location": { "type": "string" },
          "size": { "type": "string" },
          "type": { "type": "string" },
          "website": { "type": "string" }
        }
      },
      "keyTakeaways": {
        "type": "array",
        "items": { "type": "string" }
      },
      "ratingsBreakdown": {
        "type": "object",
        "properties": {
          "cultureAndValues": { "type": "number" },
          "diversityAndInclusion": { "type": "number" },
          "workLifeBalance": { "type": "number" },
          "seniorManagement": { "type": "number" },
          "compensationAndBenefits": { "type": "number" },
          "careerOpportunities": { "type": "number" }
        }
      },
      "reviewSnippets": {
        "type": "array",
        "items": {
          "type": "object",
          "properties": {
            "role": { "type": "string" },
            "pros": { "type": "array", "items": { "type": "string" } },
            "cons": { "type": "array", "items": { "type": "string" } }
          }
        }
      },
      "faq": {
        "type": "array",
        "items": {
          "type": "object",
          "properties": {
            "question": { "type": "string" },
            "answer": { "type": "string" }
          }
        }
      },
      "callToAction": { "type": "string" }
    },
    "required": ["text"]
  }
}

parameters.functionCode:
items[0].binary = {
  data: {
    data: new Buffer(JSON.stringify(items[0].json, null, 2)).toString('base64')
  }
};
return items;
