// Robust SignWell link extractor for a Gmail thread item

const msgs = $json.messages || ($json.payload ? [$json] : []);
const out = { decodedText: '', downloadLink: '', extractedFilename: '' };

// helpers
function b64urlDecode(s = '') {
  s = s.replace(/-/g, '+').replace(/_/g, '/');
  while (s.length % 4) s += '=';
  return Buffer.from(s, 'base64').toString('utf8');
}
function qpDecode(s = '') {
  // remove soft line breaks and decode =XX
  return s.replace(/=\r?\n/g, '').replace(/=([A-Fa-f0-9]{2})/g, (_, h) => String.fromCharCode(parseInt(h, 16)));
}
function partText(part) {
  if (!part?.body?.data) return '';
  let s = b64urlDecode(part.body.data);
  const isQP = (part.headers || []).some(h => /Content-Transfer-Encoding/i.test(h.name) && /quoted-printable/i.test(h.value));
  if (isQP) s = qpDecode(s);
  return s;
}
function findFirstLinkInParts(p) {
  if (!p) return null;
  // prefer text/plain, then text/html
  if (/^text\//i.test(p.mimeType)) {
    const s = partText(p);
    const m = s.match(/https:\/\/www\.signwell\.com\/app\/signed\/[^\s"'()<>]+/i);
    if (m) return m[0].replace(/[.)]+$/, ''); // strip trailing punctuation
  }
  for (const child of (p.parts || [])) {
    const found = findFirstLinkInParts(child);
    if (found) return found;
  }
  return null;
}
function findPlainText(p) {
  if (!p) return '';
  if (p.mimeType === 'text/plain') return partText(p);
  for (const child of (p.parts || [])) {
    const s = findPlainText(child);
    if (s) return s;
  }
  return '';
}

// process first message in thread
for (const m of msgs) {
  // Subject can be top-level or in headers
  const subject = m.Subject || m.subject ||
    (m.payload?.headers || []).find(h => h.name === 'Subject')?.value || '';
  const nameMatch = subject.match(/\(([^)]+)\)/);
  if (nameMatch) out.extractedFilename = nameMatch[1].replace(/\.pdf$/i, '');

  out.downloadLink = findFirstLinkInParts(m.payload) || '';
  out.decodedText = findPlainText(m.payload) || '';
  break;
}

return [{ json: out }];

