// 1. Get the JSON string from the specific input path you provided.
const jsonString = $input.first().json.output.string;

// 2. Parse that string into a real JavaScript array.
const productArray = JSON.parse(jsonString);

// 3. Map over the parsed array to transform each product.
return productArray.map(product => {

  // Split the "Size" string into an array of objects.
  const sizesList = product.Size.split(',').map(size => {
    return { "Size": size.trim().replace(" ", "-") };
  });

  // Split the "Images" string into an array of objects.
  const imagesList = product.Images.split('||').map(url => {
    return { "Image": url.trim() };
  });

  // Create the final, structured JSON object.
  const outputData = {
    "Title": product.Title,
    "Price": product.Price,
    "Description": product.Description,
    "Category": product.Category,
    "sizes": sizesList,
    "images": imagesList
  };

  // Return the data in the standard n8n format for the next node.
  return { json: outputData };
});
