// Input: items from "Read plants" (one per row)
// Output:
//  - $json.due: array de { plant_id, plant, action, reason, lat, lon, thirst_level, indoor, weather_delay }
//  - $json.toCheckWeather: array {lat, lon}
// Frecuencys: "7d", "2w", "1m" or number of days.

function parseFreq(f) {
  if (!f) return null;
  if (typeof f === 'number') return f;
  const m = String(f).trim().match(/^(\d+)\s*([dwm])?$/i);
  if (!m) return null;
  const n = parseInt(m[1], 10);
  const unit = (m[2] || 'd').toLowerCase();
  if (unit === 'd') return n;
  if (unit === 'w') return n * 7;
  if (unit === 'm') return n * 30; 
  return n;
}

function daysBetween(a, b) {
  const A = new Date(a + 'T00:00:00');
  const B = new Date(b + 'T00:00:00');
  return Math.floor((B - A) / (1000*60*60*24));
}

const today = new Date().toISOString().slice(0,10); // YYYY-MM-DD
const due = [];
const toCheckWeather = [];
const coordSet = new Set();

for (const item of items) {
  const r = item.json;

  const actions = [
    { key: 'water',     lastKey: 'last_water', freqKey: 'water_freq' },
    { key: 'fertilize', lastKey: 'last_fert',  freqKey: 'fert_freq'  },
  ];

  for (const a of actions) {
    const freqDays = parseFreq(r[a.freqKey]);
    if (!freqDays) continue;

    const last = r[a.lastKey] ? String(r[a.lastKey]).slice(0,10) : null;
    const elapsed = last ? daysBetween(last, today) : Infinity;

    if (elapsed >= freqDays) {
      const entry = {
        plant_id: r.id,
        plant: r.plant,
        action: a.key,
        reason: last ? `due after ${elapsed}d` : 'no last date',
        lat: r.lat ? Number(r.lat) : null,
        lon: r.lon ? Number(r.lon) : null,
        weather_delay: String(r.weather_delay || '').toLowerCase() === 'true',
        thirst_level: (r.thirst_level || 'med').toLowerCase(),
        indoor: (r.indoor || '').toLowerCase(),
      };

      if (a.key === 'water' && entry.weather_delay && entry.lat && entry.lon) {
        const key = `${entry.lat},${entry.lon}`;
        if (!coordSet.has(key)) {
          coordSet.add(key);
          toCheckWeather.push({ lat: entry.lat, lon: entry.lon });
        }
      }

      due.push(entry);
    }
  }
}

return [{ json: { today, due, toCheckWeather } }];

// WeatherGate (single-input, merged)
// Upstream: Merge (Combine) of DecideDue+OpenWeather, each tagged with json.source = "decide" | "weather"

function rainScore(forecast) {
  const now = Date.now() / 1000, win = 24 * 3600;
  let recent = 0, upcoming = 0;
  const list = (forecast && forecast.list) || [];
  for (const e of list) {
    const t = e.dt, mm = e.rain && e.rain['3h'] ? e.rain['3h'] : 0;
    if (typeof t !== 'number') continue;
    if (t < now && (now - t) <= win) recent += mm;
    if (t >= now && (t - now) <= win) upcoming += mm;
  }
  return { recent, upcoming };
}
const coordKey = (lat, lon) => {
  const la = Number(lat), lo = Number(lon);
  if (!Number.isFinite(la) || !Number.isFinite(lo)) return null;
  return `${la.toFixed(4)},${lo.toFixed(4)}`;
};

// Read everything from single input
const all = $input.all();
const decide = all.find(i => (i.json?.source || '').toLowerCase() === 'decide')?.json || {};
const weatherItems = all.filter(i => (i.json?.source || '').toLowerCase() === 'weather')
                        .map(i => i.json?.body ?? i.json ?? {});

// Normalize controller
const today = decide.today || new Date().toISOString().slice(0,10);
const due = Array.isArray(decide.due) ? decide.due : [];

// Build coord ‚Üí rain map
const wxMap = new Map();
for (const w of weatherItems) {
  const lat = w?.city?.coord?.lat ?? w?.coord?.lat ?? w?.lat;
  const lon = w?.city?.coord?.lon ?? w?.coord?.lon ?? w?.lon;
  const k = coordKey(lat, lon);
  if (!k) continue;
  if (!wxMap.has(k)) wxMap.set(k, { ...rainScore(w), raw: w });
}

// Decide skip / send
const out = [];
for (const d of due) {
  const act = d.action;
  const lat = d.lat ?? d.latitude ?? null;
  const lon = d.lon ?? d.longitude ?? null;
  const indoor = String(d.indoor || '').toLowerCase();
  const thirst = String(d.thirst_level || 'med').toLowerCase();
  const weatherDelay = String(d.weather_delay || '').toLowerCase() === 'true';
  let note = d.reason || '';
  let skip = false;

  if (act === 'water' && weatherDelay && lat != null && lon != null) {
    let recentThresh = 2, upcomingThresh = 3;
    if (thirst === 'low') { recentThresh = 1; upcomingThresh = 2; }
    if (thirst === 'high') { recentThresh = 4; upcomingThresh = 6; }
    const wx = wxMap.get(coordKey(lat, lon));
    if (wx) {
      if (wx.recent >= recentThresh) {
        skip = true;
        note += (note ? ' | ' : '') + `skipped: ${wx.recent.toFixed(1)}mm rain last 24h`;
      } else if (wx.upcoming >= upcomingThresh && indoor !== 'indoor') {
        skip = true;
        note += (note ? ' | ' : '') + `delayed: ${wx.upcoming.toFixed(1)}mm rain next 24h`;
      }
    }
  }

  if (!skip) {
    out.push({
      json: {
        plant_id: d.plant_id || d.id || d.plantId,
        plant: d.plant || d.name || d.title,
        action: act,
        message: `Time to ${act} **${d.plant || d.name || d.title || 'your plant'}** (${note || 'due today'}).`,
        updateFlags: { [`last_${act}`]: today }
      }
    });
  }
}

if (!out.length) {
  return [{ json: { noop: true, reason: 'no-due-or-all-skipped', today, dueCount: due.length } }];
}
return out;

// Mode: Run once for each item

const q = $json.query || {};              // comes from the Webhook node
const id = (q.plant || '').toString().trim();
const action = ((q.action || 'water') + '').toLowerCase();
const today = new Date().toISOString().slice(0,10);

const allowed = ['water', 'fertilize', 'repot'];
if (!id || !allowed.includes(action)) {
  return { json: { ok: false, error: 'Missing/invalid id or action', debug: $json } };
}

return {
  json: {
    ok: true,
    id,
    action,
    today,
    columnToUpdate: `last_${action}`   // e.g. last_water
  }
};

// Modo: Run once for each item
const id     = ($json.id || '').toString();
const action = ($json.action || '').toString().toLowerCase();
const today  = ($json.today || new Date().toISOString().slice(0,10)).toString();
const col    = ($json.columnToUpdate || '').toString();

// Mapa de acciones (infinitivo ‚Üí pasado)
const pastMap = { water: 'Watered', fertilize: 'Fertilized', repot: 'Repotted' };
const verbPast = pastMap[action] || action;

const html = `<!doctype html>
<html>
<head>
  <meta charset="utf-8">
  <title>${verbPast} ‚Ä¢ ${id}</title>
  <meta name="viewport" content="width=device-width,initial-scale=1">
  <style>
    body{font-family:system-ui,-apple-system,Segoe UI,Roboto,Helvetica,Arial,sans-serif;margin:0;background:#f6f7fb;color:#111}
    .wrap{max-width:640px;margin:40px auto;padding:0 16px}
    .card{background:#fff;border-radius:14px;box-shadow:0 6px 24px rgba(0,0,0,.08);padding:22px}
    h1{margin:0 0 10px;font-size:22px}
    .ok{display:inline-block;background:#22c55e;color:#fff;padding:6px 10px;border-radius:999px;font-weight:600;margin-bottom:12px}
    .kv{margin-top:8px;line-height:1.7}
    .kv b{display:inline-block;width:160px}
    .foot{color:#666;margin-top:16px;font-size:13px}
  </style>
</head>
<body>
  <div class="wrap">
    <div class="card">
      <div class="ok">‚úÖ Recorded</div>
      <h1>${verbPast}</h1>
      <div class="kv">
        <div><b>Plant ID</b> ${id}</div>
        <div><b>Action</b> ${verbPast}</div>
        <div><b>Updated field</b> ${col}</div>
        <div><b>Date</b> ${today}</div>
      </div>
      <p class="foot">You can close this tab now.</p>
    </div>
  </div>
</body>
</html>`;

return [{ json: { body: html } }];
