// Extract all booking data from Calendly webhook
const payload = $json.body.payload;
const scheduledEvent = payload.scheduled_event;
const location = scheduledEvent.location;

// Extract Zoom meeting ID
const zoomMeetingId = location?.data?.id || null;
const zoomJoinUrl = location?.join_url || null;

if (!zoomMeetingId) {
  throw new Error('No Zoom meeting ID found - is this a Zoom meeting?');
}

return [{
  json: {
    // IDs
    booking_id: payload.uri,
    meeting_id: parseInt(zoomMeetingId),
    event_type_uri: scheduledEvent.event_type,

    // Meeting details
    zoom_join_url: zoomJoinUrl,
    zoom_password: location?.data?.password || null,
    event_name: scheduledEvent.name,
    start_time: scheduledEvent.start_time,
    end_time: scheduledEvent.end_time,

    // Attendee info
    email: payload.email,
    name: payload.name,

    // Tracking
    status: 'pending',
    created_at: new Date().toISOString()
  }
}];

// Extract Zoom meeting ID from webhook and convert to number
const meetingId = parseInt($json.body?.payload?.object?.id);

if (!meetingId) {
  throw new Error('No meeting ID in Zoom webhook');
}

return [{
  json: {
    meeting_id: meetingId,
    meeting_uuid: $json.body?.payload?.object?.uuid,
    ended_at: new Date().toISOString()
  }
}];

// Check if expected attendee actually joined
const bookingData = $('Get Booking from Database').first().json;
const expectedEmail = bookingData.email.toLowerCase();
const participants = $json.participants || [];

const attended = participants.some(p => 
  p.user_email && p.user_email.toLowerCase() === expectedEmail
);

return [{
  json: {
    ...bookingData,
    attended: attended,
    participant_count: participants.length,
    participants_list: participants.map(p => p.name || p.user_email).join(', ')
  }
}];

