// Extract organization URI
const response = $input.first().json;
const orgUri = response.resource.current_organization;

if (!orgUri) {
  throw new Error('No organization found. You might be on a personal account.');
}

console.log('‚úÖ Your Organization URI:', orgUri);

return [{
  json: {
    organization_uri: orgUri,
    user_uri: response.resource.uri,
    user_name: response.resource.name,
    user_email: response.resource.email
  }
}];

// Parse webhook creation response
const response = $input.first().json;

if (response.resource) {
  console.log('‚úÖ WEBHOOK CREATED SUCCESSFULLY!');
  console.log('Webhook URI:', response.resource.uri);
  console.log('Webhook URL:', response.resource.url);
  console.log('Events:', response.resource.events);
  console.log('State:', response.resource.state);

  return [{
    json: {
      success: true,
      webhook_uri: response.resource.uri,
      webhook_url: response.resource.url,
      events: response.resource.events,
      state: response.resource.state,
      message: '‚úÖ Webhook subscription created! Calendly will now POST to your n8n webhook when bookings happen.'
    }
  }];
} else {
  throw new Error('Webhook creation failed: ' + JSON.stringify(response));
}

const crypto = require('crypto');

// Your Zoom webhook secret token
const ZOOM_WEBHOOK_SECRET = 'YOUR_ZOOM_WEBHOOK_SECRET';

const body = $json.body;

// Check if this is a validation request
if (body?.event === 'endpoint.url_validation') {
  const plainToken = body.payload.plainToken;

  // Hash the plainToken using HMAC SHA-256
  const encryptedToken = crypto
    .createHmac('sha256', ZOOM_WEBHOOK_SECRET)
    .update(plainToken)
    .digest('hex');

  return [{
    plainToken: plainToken,
    encryptedToken: encryptedToken
  }];
}

// For normal webhook events, pass through
return [$json];
