const data = $json.output?.[0] ?? {};
return [{
  url: data.url,
  decodo_score: data.decodo_score,
  top_issues: data.top_issues,
  priority: data.priority,
  suggested_title: data.suggested_title,
  suggested_meta: data.suggested_meta,
  status: data.status,
  last_checked: data.last_checked,
  // optional: ubah jadi JSON string supaya tetap 1 kolom
  recommended_actions: (data.recommended_actions || []).join('\n'),
  all_issues: JSON.stringify(data.all_issues || [])
}];

const data = $json.output?.[0];
if (!data || !Array.isArray(data.all_issues)) return [];
return data.all_issues.map(issue => ({
  url: data.url,
  id: issue.id,
  title: issue.title,
  category: issue.category,
  severity: issue.severity,
  evidence: issue.evidence,
  recommendation: issue.recommendation,
  impact: issue.impact,
  effort: issue.effort,
  score_delta: issue.score_delta,
  examples: (issue.examples || []).join("; ")
}));
