const filteredList = $input.all().map(item => {
  return {
    json: {
      path: item.json.FileName
    }
  };
});


const seen = new Set();
const unique = [];

for (const item of filteredList) {
  const name = item.json.path;
  if (!seen.has(name)) {
    seen.add(name);
    unique.push(item);
  }
}

return unique;

const allItems = $input.all();

// Extract arrays 
const googlePaths = allItems.find(i => i.json.googlesheetsPaths)?.json.googlesheetsPaths || [];
const githubPaths = allItems.find(i => i.json.githubPaths)?.json.githubPaths || [];

// Compute difference: elements in githubPaths not in googlesheetsPaths
const difference = githubPaths.filter(path => !googlePaths.includes(path));

// Return as n8n item array
return difference.map(path => ({ json: { path } }));

